INSERT INTO `sentences` VALUES (12120,'Lokacija, obezbeđen besplatni parking i ljubaznost. Smeštaj je odmah pored Većnice sa pogledom na Miljacku i Trebevic.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12121,'Osoblje je bilo više nego angažirano dati nam potrebne informacije, pobrinuti za naš dolazak i ulazak u sobu, parkirni prostor. Lokacija je više nego odlična, odmah iza Vijećnice, s mogućnošću besplatnog parkiranja što omogoćuje dodatnu bez-skrbnost.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12122,'Ljubazni domaćini i super lokacija','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12123,'Odlicna lokacija. Miran kraj.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12124,'Blizina centra.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12125,'Odlična lokacija, pet minuta pješice do Baščaršije, apartman predivan i čist, vlasnici iznimno susretljivi-moje tople preporuke!','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12126,'Lokacija i ceo smestaj','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12127,'Lokacija savrsena, gospodja koja nas je docekala ljubazna i bas divna. Apartman prostran, ima sve sto je potrebno. Detaljno nam je objasnjeno dje se smjestaj nalazi tako da smo ga odmah nasli.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12128,'Skvělý hostitel, krásné místo.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12129,'Sve je bilo u redu.prijazna domačinka,prijatan ambijent,','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12130,'Ljubazna domaćica,čist apartman na dobrom mjestu za obilazak Sarajeva, garaža.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12131,'Gospoda Denza nas dočekala kao pravi domaćin u toplom stanu,na izvrsnoj lokaciji par minuta hoda do grada..','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12132,'Udobno, toplo, čisto, gostoljubivo, blizu do centra, sve kako treba i više!','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12133,'Svaka preporuka za smeštaj. Lokacija savršena, smeštaj odličan.','pozitivan',1,0,0,'2025-03-26 22:04:50'),(12134,'Sve je izuzetno, lokacija fenomenalna, vlasnica iz